// client/src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  // News state
  const [news, setNews] = useState([]);
  // Chatbot state
  const [chatMessage, setChatMessage] = useState('');
  const [chatResponse, setChatResponse] = useState('');
  // Incident form
  const [incidentDescription, setIncidentDescription] = useState('');
  const [incidentLocation, setIncidentLocation] = useState('');
  const [incidents, setIncidents] = useState([]);

  // BASE URL of your FastAPI server
  // If running locally: "http://127.0.0.1:8000"
  const API_BASE = process.env.REACT_APP_API_BASE || "http://127.0.0.1:8000";

  // 1) Fetch news on load
  useEffect(() => {
    axios.get(`${API_BASE}/api/news`)
      .then((res) => setNews(res.data.news))
      .catch((err) => console.error(err));
  }, [API_BASE]);

  // 2) Fetch incidents
  useEffect(() => {
    axios.get(`${API_BASE}/api/incidents`)
      .then((res) => setIncidents(res.data))
      .catch((err) => console.error(err));
  }, [API_BASE]);

  // Handler: Send message to Chatbot
  const handleChatSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${API_BASE}/api/chatbot`, {
        message: chatMessage
      });
      setChatResponse(res.data.bot_reply);
    } catch (error) {
      console.error(error);
    }
  };

  // Handler: Create Incident
  const handleIncidentSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_BASE}/api/incidents`, {
        description: incidentDescription,
        location: incidentLocation
      });
      // After creating, refetch incidents
      const res = await axios.get(`${API_BASE}/api/incidents`);
      setIncidents(res.data);

      // Clear form
      setIncidentDescription('');
      setIncidentLocation('');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div style={{ margin: "20px" }}>
      <h1>HSE GO MVP</h1>

      {/* NEWS SECTION */}
      <section>
        <h2>News</h2>
        {news.map((item, index) => (
          <div key={index} style={{ marginBottom: "10px" }}>
            <a href={item.link} target="_blank" rel="noreferrer">
              <strong>{item.title}</strong>
            </a>
            <p>{item.summary}</p>
          </div>
        ))}
      </section>

      {/* CHATBOT SECTION */}
      <section>
        <h2>Chatbot</h2>
        <form onSubmit={handleChatSubmit}>
          <input
            type="text"
            value={chatMessage}
            onChange={(e) => setChatMessage(e.target.value)}
            placeholder="Ask a question..."
          />
          <button type="submit">Send</button>
        </form>
        {chatResponse && <p><strong>Bot reply:</strong> {chatResponse}</p>}
      </section>

      {/* INCIDENT FORM */}
      <section>
        <h2>Report an Incident</h2>
        <form onSubmit={handleIncidentSubmit}>
          <div>
            <input
              type="text"
              value={incidentDescription}
              onChange={(e) => setIncidentDescription(e.target.value)}
              placeholder="Description"
            />
          </div>
          <div>
            <input
              type="text"
              value={incidentLocation}
              onChange={(e) => setIncidentLocation(e.target.value)}
              placeholder="Location"
            />
          </div>
          <button type="submit">Submit Incident</button>
        </form>
      </section>

      {/* INCIDENT LIST */}
      <section>
        <h2>Incidents</h2>
        {incidents.map((inc) => (
          <div key={inc.id} style={{ border: "1px solid #ccc", margin: "5px 0", padding: "5px" }}>
            <p><strong>ID:</strong> {inc.id}</p>
            <p><strong>Description:</strong> {inc.description}</p>
            <p><strong>Location:</strong> {inc.location}</p>
            <p><strong>Created:</strong> {new Date(inc.created_at).toLocaleString()}</p>
          </div>
        ))}
      </section>
    </div>
  );
}

export default App;
